    const setCount = countState[1]
    

    const buttonClick = () => {
        setCount((value) => {return value + 1})
    }
    
    return <div>
        <div>Clicks Count: { count }</div>
        <button onClick={buttonClick}>Click Me</button>
    </div>
}
